<style>

   :global(body) {
		margin: 0;
        display: grid;
        grid-template-areas: 
        "header"
        "main"
        "footer";
        grid-template-columns: 1fr;
        grid-template-rows: 6rem 1fr 10rem;
        height: 100%
	}
    a {
        text-decoration: none;
        color: inherit;
    }
    header {
        grid-area: header;
        min-height: 6rem;
        width: 100%;
        overflow: hidden;
        position: fixed;
        top: 0;
        margin: 0;
        background-color: #F7F6FF;
        z-index: 2020;
    }
    .nav-content {
        padding-left: 5rem;
        padding-right: 5rem;
        padding-top: 1.5rem;
        display: flex;
        flex-direction: row;
    }
    .logo {
        width: 15%;
        align-items: center;
    }
    .nav-links {
        /* font-family: Satoshi; */
        padding-bottom: 1rem;
        align-items: center;
        list-style: none;
        display: flex;
        width: 80%;
        bottom: 2rem;
        justify-content: center;
    }
    .nav-links li {
        margin: 1rem
    }
    .quote {
        width: 15%;
        background-color: black;
        color: #F7F6FF;
        margin-bottom: 0.5rem;
        text-align: center;
    }
    .main {
        grid-area: main;
    }
    .footer {
        grid-area: footer;
        min-height: 5rem;
        position: fixed;
        bottom: 0;
        width: 100%;
        z-index: 1010;
        background-color: #F7F6FF;
    }
</style>